INSERT INTO public.recipes (id, title, total_cook_time_minutes, ingredients, instructions, description, n_portions, thumbnail, source_url, source_name, created_at, user_id, is_public, status) VALUES ('684af985-4a7d-44e8-94d0-bb2eb2714c42', 'Mini Caprese Spiesjes – Een Italiaanse Klassieker op een Prikker', 10, '[{"name": "Spiesjes", "ingredients": [{"unit": "whole", "quantity": {"low": 20, "high": 25, "type": "range"}, "description": "cherrytomaten, gehalveerd"}, {"unit": "whole", "quantity": {"low": 20, "high": 25, "type": "range"}, "description": "mini-mozzarellabolletjes (bocconcini)"}, {"unit": "whole", "quantity": {"low": 20, "high": 25, "type": "range"}, "description": "verse basilicumblaadjes, schoon en droog"}]}, {"name": "Dressing", "ingredients": [{"unit": "tablespoon", "quantity": {"low": 2, "high": 3, "type": "range"}, "description": "olijfolie, extra vierge"}, {"unit": "tablespoon", "quantity": {"low": 2, "high": 3, "type": "range"}, "description": "balsamico azijn"}, {"unit": "pinch", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "zout en peper naar smaak"}]}, {"name": "Optioneel", "ingredients": [{"unit": "whole", "quantity": {"low": 10, "high": 12, "type": "range"}, "description": "houten prikkers, voor het opspelden"}]}]', '{"Begin met het pakken van de cherrytomaten, mozzarella en verse basilicum. Zorg ervoor dat de tomaten gehalveerd zijn om makkelijker aan de prikker te blijven zitten.","Neem een houten prikker en rijg er voorzichtig een halve cherrytomaat aan, gevolgd door een blaadje basilicum en een mini-mozzarellabolletje. Herhaal dit tot de prikker vol is of alle ingrediënten op zijn.","Meng in een klein kommetje de olijfolie, balsamico azijn, en een snufje zout en peper. Deze combinatie maakt een heerlijk frisse dressing.","Druppel de dressing bovenop de spiesjes. Laat een beetje over elk spiesje vallen voor die authentieke Italiaanse smaak.","Serveer de caprese spiesjes direct op een mooie schaal, of bewaar ze in de koelkast als je ze later wilt serveren. Ze zien er niet alleen feestelijk uit, maar smaken ook nog eens geweldig!","Tip: Gebruik houten prikkers die iets vocht hebben opgenomen in water. Dit voorkomt dat ze versplinteren en zorgt dat de ingrediënten beter blijven zitten."}', '', 4, 'http://127.0.0.1:54321/storage/v1/object/public/recipe-images/9222ebb4-abee-44cd-a361-e9d4f6ae2633.png', 'https://app.bonchef.io', 'BonChef', '2025-06-02 19:43:41.466277+00', '30e849e7-9577-4e52-a148-ba6abbdc407b', false, 'PUBLISHED');
INSERT INTO public.recipes (id, title, total_cook_time_minutes, ingredients, instructions, description, n_portions, thumbnail, source_url, source_name, created_at, user_id, is_public, status) VALUES ('42b6eae3-c1c0-4ef3-906c-7a71ffa45bbb', 'Wonderbaarlijke Bananenpannenkoekjes voor het Hele Gezin', 15, '[{"name": "Pannenkoekjes", "ingredients": [{"unit": "none", "quantity": {"low": 2, "high": 2, "type": "range"}, "description": "eieren"}, {"unit": "whole", "quantity": {"low": 2, "high": 2, "type": "range"}, "description": "rijpe bananen"}, {"unit": "tablespoon", "quantity": {"low": 1, "high": 2, "type": "range"}, "description": "plantaardige olie"}, {"unit": "tablespoon", "quantity": {"low": 0, "high": 1, "type": "range"}, "description": "honing, optioneel"}]}]', '{"Begin met het prakken van de rijpe bananen in een grote kom. Hoe rijper de bananen, hoe zoeter de pannenkoekjes zullen zijn.","Breek de eieren en voeg deze toe aan de geprakte banaan. Meng alles goed door elkaar tot je een glad beslag hebt zonder klontjes.","Verhit een eetlepel olie in een koekenpan op middelhoog vuur. Wacht even totdat de olie heet is.","Giet kleine hoeveelheden van het beslag in de pan; ongeveer twee eetlepels per pannenkoekje. Dit maakt het omdraaien makkelijker en zorgt voor het beste resultaat.","Bak de pannenkoekjes ongeveer 2-3 minuten aan elke kant tot ze goudbruin zijn. Wees voorzichtig bij het omdraaien, ze zijn wat delicater dan gewone pannenkoeken.","Herhaal dit proces totdat al het beslag op is.","Serveer de warme bananenpannenkoekjes direct, met een scheutje honing erover voor wat extra zoetigheid, als je dat lekker vindt."}', '', 2, 'http://127.0.0.1:54321/storage/v1/object/public/recipe-images/1b0a94f7-d526-4547-8f65-180082503e0c.png', 'https://app.bonchef.io', 'BonChef', '2025-06-02 19:42:31.738875+00', '30e849e7-9577-4e52-a148-ba6abbdc407b', false, 'PUBLISHED');
INSERT INTO public.recipes (id, title, total_cook_time_minutes, ingredients, instructions, description, n_portions, thumbnail, source_url, source_name, created_at, user_id, is_public, status) VALUES ('0b26057e-6b33-4ea2-bbb8-b694d2859bf1', 'Avocado Toast Perfectie met Limoen en Kiemgroenten', 10, '[{"name": "Avocado Toast", "ingredients": [{"unit": "whole", "quantity": {"low": 2, "high": 2, "type": "range"}, "description": "rijpe avocado''s, gehalveerd en pit verwijderd"}, {"unit": "milliliter", "quantity": {"low": 30, "high": 30, "type": "range"}, "description": "olijfolie van goede kwaliteit"}, {"unit": "pinch", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "zout, naar smaak"}, {"unit": "pinch", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "chili flakes, naar smaak"}, {"unit": "slice", "quantity": {"low": 4, "high": 4, "type": "range"}, "description": "volkorenbrood, geroosterd"}, {"unit": "whole", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "limoen, gehalveerd voor sap"}, {"unit": "handful", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "kiemgroenten, zoals alfalfa of radijskiemen"}]}]', '{"Begin met het roosteren van de sneetjes volkorenbrood. Zet ze in een broodrooster of onder de grill totdat ze goudbruin zijn, terwijl je de andere ingrediënten klaarmaakt.","Snijd de avocado''s doormidden, verwijder de pit en schep het vruchtvlees met een lepel in een kom. Prak de avocado met een vork tot een glad geheel, maar houd het lekker chunky voor een goede textuur.","Voeg de olijfolie, een snufje zout en de chili flakes toe aan de geprakte avocado. Meng alles samen en proef om te checken of het heerlijk op smaak is. Voeg eventueel meer zout of chili flakes toe als je van extra pit houdt.","Zodra het brood geroosterd is, verdeel je de avocado gelijkmatig over de sneetjes. Zorg ervoor dat je het mengsel helemaal tot aan de rand uitsmeert, zodat elke hap smakelijk is.","Knijp wat limoensap over de avocado toast voor een frisse, extra smaakdimensie. Verdeel de kiemgroenten over de toast voor een knapperige topping en een vitamineboost.","Serveer je smakelijke avocado toasts meteen en geniet van de heerlijke combinatie van smaken en texturen."}', '', 2, 'http://127.0.0.1:54321/storage/v1/object/public/recipe-images/a845eca7-e2db-4718-9bff-362d7b54f803.png', 'https://app.bonchef.io', 'BonChef', '2025-06-02 19:41:31.893642+00', '30e849e7-9577-4e52-a148-ba6abbdc407b', true, 'PUBLISHED');
INSERT INTO public.recipes (id, title, total_cook_time_minutes, ingredients, instructions, description, n_portions, thumbnail, source_url, source_name, created_at, user_id, is_public, status) VALUES ('c6f61b7d-42d7-40db-a354-e92f80c49416', 'Vrolijke Fruitfairy in een Kom', 10, '[{"name": "Fruitig Basisfestijn", "ingredients": [{"unit": "none", "quantity": {"low": 1, "high": 2, "type": "range"}, "description": "rijpe bananen, in plakjes"}, {"unit": "none", "quantity": {"low": 1, "high": 2, "type": "range"}, "description": "appels, geschild, in blokjes"}, {"unit": "none", "quantity": {"low": 1, "high": 2, "type": "range"}, "description": "sinaasappels, gepeld en in partjes"}]}, {"name": "Romige Yoghurt Verrassing", "ingredients": [{"unit": "gram", "quantity": {"low": 200, "high": 250, "type": "range"}, "description": "volle yoghurt (plantaardig of dierlijk naar keuze)"}]}, {"name": "Knusse Crunchy Topping", "ingredients": [{"unit": "handful", "quantity": {"low": 2, "high": 3, "type": "range"}, "description": "granola"}]}]', '{"Neem de rijpe bananen en snijd ze in plakjes. Zorg ervoor dat je een goede stevige snijplank en een scherp mes gebruikt voor dit klusje.","Doe hetzelfde met de appels. Vergeet niet de klokhuizen te verwijderen voordat ze in blokjes worden gesneden.","Pel de sinaasappels en verdeel ze in partjes. Dit geeft een lekkere frisse smaak.","Pak twee mooie kommen en verdeel het gesneden fruit gelijkmatig over de kommen. Probeer voor een mooie presentatie te zorgen.","Schep vervolgens de yoghurt op het fruit. Het is jouw feestje, dus voeg zoveel toe als je wilt!","Strooi als laatste een paar handen vol knapperige granola over de yoghurt.","Roer alles een beetje door elkaar zodat de smaken goed mengen en geniet van je vrolijke fruitkom!"}', '', 2, 'http://127.0.0.1:54321/storage/v1/object/public/recipe-images/57dab730-dfcd-48b5-a501-b0db2ff15fb1.png', 'https://app.bonchef.io', 'BonChef', '2025-06-02 19:46:20.841291+00', '30e849e7-9577-4e52-a148-ba6abbdc407b', false, 'PUBLISHED');
INSERT INTO public.recipes (id, title, total_cook_time_minutes, ingredients, instructions, description, n_portions, thumbnail, source_url, source_name, created_at, user_id, is_public, status) VALUES ('70f61e75-b48c-4312-9dea-4c77dc9a4aac', 'Romige Eiersalade met een Kick', 20, '[{"name": "Eiersalade", "ingredients": [{"unit": "whole", "quantity": {"low": 6, "high": 6, "type": "range"}, "description": "hardgekookte eieren"}, {"unit": "tablespoon", "quantity": {"low": 3, "high": 3, "type": "range"}, "description": "mayonaise"}, {"unit": "tablespoon", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "mosterd, bij voorkeur Dijon"}, {"unit": "tablespoon", "quantity": {"low": 1, "high": 2, "type": "range"}, "description": "fijngehakte verse bieslook"}, {"unit": "teaspoon", "quantity": {"low": 0.5, "high": 0.5, "type": "range"}, "description": "zout, naar smaak"}, {"unit": "teaspoon", "quantity": {"low": 0.25, "high": 0.25, "type": "range"}, "description": "versgemalen zwarte peper, naar smaak"}]}]', '{"Pel de hardgekookte eieren en snijd ze in stukje, met een vork of een eiersnijder.","Doe de gesneden eieren in een middelgrote kom. Voeg de mayonaise en mosterd toe aan de kom, en meng alles goed door elkaar. Gebruik een vork of een spatel voor het beste resultaat.","Meng de fijngehakte bieslook door de eiersalade. Proef en voeg naar smaak zout en peper toe.","Zet de eiersalade ongeveer 10 minuten in de koelkast om de smaken goed te laten mengen. Dit is een geweldige gelegenheid om de tafel te dekken of een lekker drankje klaar te maken.","Serveer de eiersalade op vers brood, crackers of als bijgerecht bij een frisse groene salade. Heerlijk als lunch of lichte avondmaaltijd."}', '', 4, 'http://127.0.0.1:54321/storage/v1/object/public/recipe-images/5d4ca753-3af4-40a1-862d-0d0f8483f716.png', 'https://app.bonchef.io', 'BonChef', '2025-06-02 19:45:19.556579+00', '30e849e7-9577-4e52-a148-ba6abbdc407b', false, 'PUBLISHED');
INSERT INTO public.recipes (id, title, total_cook_time_minutes, ingredients, instructions, description, n_portions, thumbnail, source_url, source_name, created_at, user_id, is_public, status) VALUES ('8dfda0f4-12ee-4467-80f7-0e939e838da5', 'IJs-Koffie Coolness: De Verfrissende Espresso Boost', 5, '[{"name": "Hoofdingrediënten", "ingredients": [{"unit": "milliliter", "quantity": {"low": 50, "high": 60, "type": "range"}, "description": "verse espresso"}, {"unit": "milliliter", "quantity": {"low": 150, "high": 200, "type": "range"}, "description": "havermelk, of een andere plantaardige melk naar keuze"}, {"unit": "none", "quantity": {"low": 3, "high": 5, "type": "range"}, "description": "grote ijsklontjes"}]}]', '{"Brouw een vers kopje espresso, ongeveer 50 tot 60 milliliter. Laat het even afkoelen zodat het niet al te heet meer is.","Pak een groot glas en vul het met 3 tot 5 grote ijsklontjes. De ijsklontjes zorgen voor die verfrissende koelte, dus wees niet te zuinig!","Giet de afgekoelde espresso over de ijsklontjes. Het zien stromen van de diepbruine espresso over het ijs is al een feestje op zich.","Vul nu de rest van het glas met havermelk. Dit geeft een heerlijke romige twist aan je ijskoffie.","Pak een shaker of een andere afsluitbare pot en giet hier de hele inhoud van je glas in. Shake het geheel enthousiast door elkaar. Als je geen shaker bij de hand hebt, gebruik dan een lepel om het goed te mixen. Zo zijn alle smaken perfect gemixt tot een goddelijk drankje.","Giet de gemixte ijskoffie terug in je glas en geniet direct van deze zelfgemaakte verkoeling."}', '', 1, 'http://127.0.0.1:54321/storage/v1/object/public/recipe-images/620bd29e-bc7c-42f3-9028-d1b5acac7b19.png', 'https://app.bonchef.io', 'BonChef', '2025-06-02 19:53:12.54063+00', '2a9be8a3-6a03-4819-8e9f-9d04b3a6ea2e', true, 'PUBLISHED');
INSERT INTO public.recipes (id, title, total_cook_time_minutes, ingredients, instructions, description, n_portions, thumbnail, source_url, source_name, created_at, user_id, is_public, status) VALUES ('5b02c190-188c-450f-bbb3-eb19d40db92d', 'Verfrissende Japanse Komkommersalade met Sesamaccenten', 15, '[{"name": "Komkommer Salade", "ingredients": [{"unit": "whole", "quantity": {"low": 1, "high": 2, "type": "range"}, "description": "komkommers"}, {"unit": "tablespoon", "quantity": {"low": 2, "high": 3, "type": "range"}, "description": "rijstazijn"}, {"unit": "tablespoon", "quantity": {"low": 1, "high": 2, "type": "range"}, "description": "sojasaus"}, {"unit": "teaspoon", "quantity": {"low": 1, "high": 2, "type": "range"}, "description": "sesamzaadjes, geroosterd"}]}]', '{"Was de komkommers grondig en snijd ze in dunne plakjes. Gebruik hiervoor een mandoline of een scherp mes voor gelijkmatige plakjes.","Doe de komkommerplakjes in een grote kom. Voeg de rijstazijn en de sojasaus toe en meng het geheel goed door elkaar zodat de smaken kunnen intrekken.","Rooster de sesamzaadjes licht in een droge koekenpan op middelhoog vuur. Dit duurt slechts een paar minuten en je zult merken dat de zaadjes lichtbruin en geurig worden.","Strooi de geroosterde sesamzaadjes over de komkommersalade en meng het nogmaals kort door.","Serveer de salade direct voor de beste frisheid. Je kunt deze salade ook van tevoren maken en in de koelkast bewaren; de smaken worden alleen maar intenser!"}', '', 4, 'http://127.0.0.1:54321/storage/v1/object/public/recipe-images/08ea5ce9-0d77-4fb0-b466-1736604281fc.png', 'https://app.bonchef.io', 'BonChef', '2025-06-02 19:53:16.985284+00', '2a9be8a3-6a03-4819-8e9f-9d04b3a6ea2e', true, 'PUBLISHED');
INSERT INTO public.recipes (id, title, total_cook_time_minutes, ingredients, instructions, description, n_portions, thumbnail, source_url, source_name, created_at, user_id, is_public, status) VALUES ('f05e2a23-3a07-4e29-8435-de879cc5537c', 'Verfrissende Linzensalade met een Citrustwist', 25, '[{"name": "Hoofdingrediënten", "ingredients": [{"unit": "gram", "quantity": {"low": 200, "high": 250, "type": "range"}, "description": "gedroogde linzen (of 1 blik uitgelekte linzen voor gemak)"}, {"unit": "whole", "quantity": {"low": 2, "high": 3, "type": "range"}, "description": "rijpe tomaten, in blokjes gesneden"}, {"unit": "whole", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "rode ui, fijngehakt"}]}, {"name": "Verse kruiden en smaakmakers", "ingredients": [{"unit": "bunch", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "verse peterselie, fijngehakt"}, {"unit": "whole", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "citroen, geperst tot sap"}]}, {"name": "Optionele ingrediënten en tips", "ingredients": [{"unit": "tablespoon", "quantity": {"low": 1, "high": 2, "type": "range"}, "description": "olijfolie voor extra smaak"}, {"unit": "pinch", "quantity": {"low": 1, "high": 2, "type": "range"}, "description": "zout en peper naar smaak"}]}]', '{"Begin met het koken van de linzen. Spoel ze af en kook ze in een pan met ruim water gedurende ongeveer 20 minuten, of totdat ze beetgaar zijn. Giet ze daarna af en laat goed afkoelen. Als je voor bliklinzen kiest, hoef je alleen te spoelen en af te laten lekken.","Terwijl de linzen koken, kun je de groenten voorbereiden. Snijd de tomaten in kleine blokjes en hak de rode ui fijn. Doe ze in een ruime kom.","Hak de peterselie fijn en voeg deze toe aan de kom met de tomaten en rode ui.","Zodra de linzen zijn afgekoeld, voeg je ze toe aan de kom met de groenten.","Pers de citroen uit en giet het sap over de linzensalade. Voeg naar smaak olijfolie, zout en peper toe. Meng alles goed door elkaar zodat de smaken zich goed kunnen mengen.","Je linzensalade is nu klaar om geserveerd te worden. Voor het beste resultaat, laat het 10 minuten staan zodat alle smaken goed intrekken. Geniet ervan als een lichte lunch of als bijgerecht!"}', '', 4, 'http://127.0.0.1:54321/storage/v1/object/public/recipe-images/9ee5cd0b-a646-40be-a823-8b81f57ea1e9.png', 'https://app.bonchef.io', 'BonChef', '2025-06-02 19:57:03.028318+00', '2a9be8a3-6a03-4819-8e9f-9d04b3a6ea2e', true, 'PUBLISHED');
INSERT INTO public.recipes (id, title, total_cook_time_minutes, ingredients, instructions, description, n_portions, thumbnail, source_url, source_name, created_at, user_id, is_public, status) VALUES ('9e234a47-0025-49c3-ad46-65f8955e85b1', 'Vliegensvlugge Gnocchi Met Homemade Pesto', 20, '[{"name": "Gnocchi", "ingredients": [{"unit": "gram", "quantity": {"low": 500, "high": 500, "type": "range"}, "description": "gnocchi, vers of uit het koelschap"}]}, {"name": "Pesto", "ingredients": [{"unit": "gram", "quantity": {"low": 40, "high": 60, "type": "range"}, "description": "verse basilicum"}, {"unit": "gram", "quantity": {"low": 30, "high": 50, "type": "range"}, "description": "geraspte Parmezaanse kaas"}, {"unit": "gram", "quantity": {"low": 40, "high": 60, "type": "range"}, "description": "pijnboompitten, licht geroosterd"}, {"unit": "clove", "quantity": {"low": 1, "high": 2, "type": "range"}, "description": "knoflook"}, {"unit": "milliliter", "quantity": {"low": 100, "high": 120, "type": "range"}, "description": "extra vierge olijfolie"}, {"unit": "none", "quantity": {"low": 0.5, "high": 1, "type": "range"}, "description": "citroen, sap hiervan"}, {"unit": "pinch", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "zout en peper naar smaak"}]}, {"name": "Optionele toppings", "ingredients": [{"unit": "gram", "quantity": {"low": 50, "high": 70, "type": "range"}, "description": "rucola"}, {"unit": "gram", "quantity": {"low": 20, "high": 30, "type": "range"}, "description": "extra Parmezaanse kaas voor garnering"}]}]', '{"Breng een grote pan met water aan de kook en voeg een snufje zout toe. Voeg de gnocchi toe zodra het water kookt en kook ze tot ze beginnen te drijven. Dit duurt meestal zo''n 3 minuten.","Terwijl de gnocchi koken, maak je de pesto. In een keukenmachine combineer je de basilicum, Parmezaanse kaas, geroosterde pijnboompitten, knoflook en een scheutje citroensap. Mix tot alles goed mengt.","Giet de gnocchi af en bewaar een kopje van het kookwater om te gebruiken als de pesto te dik is.","Voeg de olijfolie langzaam toe aan de pesto terwijl de machine draait, totdat je een mooie, gladde saus hebt. Breng de pesto op smaak met zout en peper.","Meng de gnocchi met de pesto in de pan op laag vuur. Voeg een beetje van het gnocchi-kookwater toe als de saus te dik lijkt om een romige textuur te krijgen.","Serveer de gnocchi met een handvol rucola en extra schaafjes Parmezaanse kaas voor een luxe toets. Geniet meteen van dit makkelijke en smakelijke gerecht!"}', '', 4, 'http://127.0.0.1:54321/storage/v1/object/public/recipe-images/4cb605ad-f33e-4e11-92b0-f08eb00b15a1.png', 'https://app.bonchef.io', 'BonChef', '2025-06-02 19:49:07.74455+00', '30e849e7-9577-4e52-a148-ba6abbdc407b', true, 'PUBLISHED');
INSERT INTO public.recipes (id, title, total_cook_time_minutes, ingredients, instructions, description, n_portions, thumbnail, source_url, source_name, created_at, user_id, is_public, status) VALUES ('2ddd5a2d-ccf1-40df-a573-9a7c0d84b026', 'Fris Groene Doperwtenspread met een Tikkeltje Munt Magie', 15, '[{"name": "Doperwtenspread", "ingredients": [{"unit": "gram", "quantity": {"low": 500, "high": 500, "type": "range"}, "description": "doperwten, vers of diepvries"}, {"unit": "whole", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "citroen, voor sap en schil"}, {"unit": "tablespoon", "quantity": {"low": 3, "high": 3, "type": "range"}, "description": "olijfolie, extra vierge"}, {"unit": "bunch", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "verse munt, blaadjes geplukt"}, {"unit": "pinch", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "peper en zout, naar smaak"}]}]', '{"Begin met het blancheren van de doperwten. Breng een pan water aan de kook, voeg de doperwten toe en kook ze 2 tot 3 minuten tot ze net zacht zijn. Giet ze dan af en spoel onder koud water om het kookproces te stoppen.","Rasp de schil van de citroen en pers daarna het sap uit. Houd dit even apart.","Doe de afgekoelde doperwten in een keukenmachine, samen met een scheutje citroensap, de geraspte schil, en de olijfolie. Pulseer een paar keer tot je een grove puree krijgt.","Voeg de muntblaadjes toe en mix opnieuw tot de spread glad en mooi groen is. Proef en voeg naar smaak peper en zout toe. Tippie: Voeg extra citroensap toe als je van een frisse toon houdt.","Serveer de doperwtenspread op vers geroosterd brood of als dip voor knapperige rauwkost."}', '', 4, 'http://127.0.0.1:54321/storage/v1/object/public/recipe-images/a2010466-6492-4c08-b7c5-7fef26ed9042.png', 'https://app.bonchef.io', 'BonChef', '2025-06-02 19:44:29.272289+00', '30e849e7-9577-4e52-a148-ba6abbdc407b', false, 'PUBLISHED');
INSERT INTO public.recipes (id, title, total_cook_time_minutes, ingredients, instructions, description, n_portions, thumbnail, source_url, source_name, created_at, user_id, is_public, status) VALUES ('e8b77524-da68-4e37-89dc-57d86a4ee334', 'Krokante Hummus Wrap Fiesta', 15, '[{"name": "Voor de wrap", "ingredients": [{"unit": "whole", "quantity": {"low": 4, "high": 4, "type": "range"}, "description": "volkoren wraps"}, {"unit": "gram", "quantity": {"low": 300, "high": 300, "type": "range"}, "description": "hummus, kies je favoriete smaak"}, {"unit": "whole", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "kleine komkommer"}, {"unit": "whole", "quantity": {"low": 2, "high": 2, "type": "range"}, "description": "wortelen"}, {"unit": "whole", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "rode paprika"}, {"unit": "handful", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "gemengde sla, gebruik restjes"}, {"unit": "handful", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "verse muntblaadjes, optioneel voor een frisse touch"}, {"unit": "tablespoon", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "citroensap, vers geperst"}]}]', '{"Begin met de voorbereidingen. Was de komkommer, wortelen en paprika grondig. Snijd de komkommer in dunne reepjes en rasp de wortelen fijn. Verwijder de zaadjes uit de paprika en snijd deze ook in smalle reepjes.","Nu komt het smeuïge deel! Spreid een royale hoeveelheid hummus over elke volkoren wrap. Vergeet niet de randjes vrij te laten; het bespaart je later geknoei.","Besprenkel de hummus dunnetjes met het versgeperste citroensap voor een frisse kick.","Nu is het tijd om te bouwen. Verdeel een handvol gemengde sla over de hummus en voeg vervolgens de komkommer, wortel en paprika toe. Wil je iets extra verfrissends? Strooi dan wat verse muntblaadjes over de groenten.","En rol maar! Begin aan de ene kant van de wrap en rol hem stevig op. Zorg ervoor dat de vulling netjes binnen blijft zodat je later niet morsen.","Je wraps zijn klaar om geserveerd te worden! Snijd ze eventueel schuin doormidden voor een feestelijke presentatie en hap erin."}', '', 4, 'http://127.0.0.1:54321/storage/v1/object/public/recipe-images/daf58feb-505c-4cc9-8cf0-6a706ea38fce.png', 'https://app.bonchef.io', 'BonChef', '2025-06-02 19:52:46.970186+00', '2a9be8a3-6a03-4819-8e9f-9d04b3a6ea2e', true, 'PUBLISHED');
INSERT INTO public.recipes (id, title, total_cook_time_minutes, ingredients, instructions, description, n_portions, thumbnail, source_url, source_name, created_at, user_id, is_public, status) VALUES ('81a68121-a4a0-4993-a863-d48f07afbd63', 'Krokante Kikkererwtensnacks: Een Crispy Tussendoortje', 30, '[{"name": "Kikkererwten", "ingredients": [{"unit": "gram", "quantity": {"low": 400, "high": 400, "type": "range"}, "description": "een blik kikkererwten, uitgelekt en afgespoeld"}]}, {"name": "Kruidenmix", "ingredients": [{"unit": "tablespoon", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "olijfolie"}, {"unit": "teaspoon", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "paprikapoeder"}, {"unit": "teaspoon", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "knoflookpoeder"}, {"unit": "teaspoon", "quantity": {"low": 0.5, "high": 0.5, "type": "range"}, "description": "zout"}, {"unit": "teaspoon", "quantity": {"low": 0.5, "high": 0.5, "type": "range"}, "description": "gemalen zwarte peper"}]}]', '{"Verwarm de oven voor op 200 graden Celsius zodat deze lekker warm is als de kikkererwten erin gaan.","Spoel de kikkererwten goed af onder koud water en laat ze uitlekken in een vergiet. Dep ze daarna droog met een schone theedoek of keukenpapier. Hoe droger de kikkererwten, hoe krokanter ze worden.","Doe de droge kikkererwten in een kom en voeg de olijfolie, paprikapoeder, knoflookpoeder, zout en zwarte peper toe. Voor een pittige variant kun je een snufje cayennepeper toevoegen!","Meng de kikkererwten goed met de kruiden en zorg ervoor dat ze allemaal een mooi laagje hebben.","Verspreid de gekruide kikkererwten gelijkmatig over een bakplaat bekleed met bakpapier. Zorg ervoor dat ze niet overlappen, dit helpt ze knapperig te bakken.","Schuif de bakplaat in de oven en bak de kikkererwten in ongeveer 25 minuten knapperig. Schud ze halverwege de baktijd even goed door om gelijkmatig te bakken.","Haal de kikkererwten uit de oven als ze goudbruin en knapperig zijn naar wens. Laat ze even afkoelen voordat je aanvalt, want ze worden na het bakken nog iets krokanter."}', '', 4, 'http://127.0.0.1:54321/storage/v1/object/public/recipe-images/722a71df-a99d-4a6b-a99b-709a92d81af4.png', 'https://app.bonchef.io', 'BonChef', '2025-06-02 19:53:39.565835+00', '2a9be8a3-6a03-4819-8e9f-9d04b3a6ea2e', true, 'PUBLISHED');
INSERT INTO public.recipes (id, title, total_cook_time_minutes, ingredients, instructions, description, n_portions, thumbnail, source_url, source_name, created_at, user_id, is_public, status) VALUES ('a8af6a96-47e0-49e0-863f-0ec55eb07008', 'Romige citroenrisotto: De smaak van frisheid en comfort in één', 35, '[{"name": "Basis Risotto", "ingredients": [{"unit": "gram", "quantity": {"low": 300, "high": 320, "type": "range"}, "description": "risotto rijst (zoals Arborio)"}, {"unit": "whole", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "ui gesnipperd"}, {"unit": "tablespoon", "quantity": {"low": 2, "high": 3, "type": "range"}, "description": "olijfolie"}, {"unit": "liter", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "groentebouillon (kan eventueel gemaakt worden van blokjes)"}, {"unit": "gram", "quantity": {"low": 50, "high": 60, "type": "range"}, "description": "boter"}]}, {"name": "Citroensmaken", "ingredients": [{"unit": "whole", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "citroen (zowel rasp als sap)"}, {"unit": "gram", "quantity": {"low": 100, "high": 120, "type": "range"}, "description": "geraspte Parmezaanse kaas"}, {"unit": "pinch", "quantity": {"low": 1, "high": 1, "type": "range"}, "description": "zout en peper naar smaak"}]}]', '{"Verhit de olijfolie in een ruime pan op middelhoog vuur. Voeg de gesnipperde ui toe en bak deze in ongeveer 2 tot 3 minuten glazig.","Roer de risottorijst door de glazige ui en bak tot de rijstkorrels licht beginnen te glanzen. Dit zou ongeveer een minuut moeten duren.","Fruit ondertussen de citroenrasp kort mee voor een extra geurige punch.","Voeg een soeplepel van de hete groentebouillon toe aan de rijst en roer goed. Wacht tot de bouillon bijna is opgenomen voordat je een nieuwe lepel toevoegt.","Herhaal dit proces totdat de rijst beetgaar is en een romige textuur heeft, dit duurt ongeveer 18 tot 20 minuten.","Haal de pan van het vuur en voeg de boter, de Parmezaanse kaas en het citroensap toe. Roer goed door zodat alles smelt en de risotto lekker romig wordt.","Breng op smaak met zout en peper. Verdeel de risotto over de borden en serveer direct voor een frisse en luxe eetervaring."}', '', 4, 'http://127.0.0.1:54321/storage/v1/object/public/recipe-images/a3c5b55f-2e1e-4093-970a-23d4160fc533.png', 'https://app.bonchef.io', 'BonChef', '2025-06-03 20:29:01.019505+00', '2a9be8a3-6a03-4819-8e9f-9d04b3a6ea2e', false, 'PUBLISHED');
